import React from 'react';
import { IonItem, IonLabel, IonDatetime,IonInput } from '@ionic/react';
import { ItemProps } from './ItemProps';
import {text} from "ionicons/icons";

interface ItemPropsExt extends ItemProps {
    onEdit: (id?: string) => void;
}

const Item: React.FC<ItemPropsExt> = ({ id, data,ora,serviciul,nrPers,nume,liber, onEdit }) => {
    return (
        <IonItem onClick={() => onEdit(id)}>
            <IonLabel>Data: <IonDatetime displayFormat="MM/DD/YYYY">{data}</IonDatetime></IonLabel>
            <IonLabel>Ora: <IonDatetime displayFormat="HH:mm">{ora}</IonDatetime></IonLabel>
            <IonLabel>Serviciul: {serviciul}</IonLabel>
            <IonLabel>Numarul persoanelor: <IonInput value = {text}>{nrPers}</IonInput></IonLabel>
            <IonLabel>Nume: {nume}</IonLabel>
            <IonLabel>Liber: {liber}</IonLabel>
        </IonItem>
    );
};

export default Item;
